protocol BlogInteractorInput {
	func loadBlog(with id: Int)
}

// MARK: Implementation

class BlogInteractor: BlogInteractorInput {

	weak var output: BlogInteractorOutput!
	
	func loadBlog(with id: Int) {
		APIBlogPost(onSuccess: { (blog) in
			print(blog)
		}, onFailure: { error in
			print(error)
		},
			 id: id
		)
		.dispatch()
		
	}

}
		