import SharedCodeFramework

class LanguageChoicePresenter {
	
	weak var view: LanguageChoiceViewInput!
	var interactor: LanguageChoiceInteractorInput!
	var router: LanguageChoiceRouterInput!

}


// MARK: ViewController output protocol

protocol LanguageChoiceViewOutput {
	func viewIsReady()
}

extension LanguageChoicePresenter: LanguageChoiceViewOutput {
	func viewIsReady() {
		view.setupInitialState()
		view.setupView(with:
			LanguageChoiceView.Props(
				chooseKazakh: Command {
					AppSettings.language = .kazakh
					self.router.presentGreeting(with: self.view)
				},
				chooseRus: Command {
					AppSettings.language = .russian
					self.router.presentGreeting(with: self.view)
			})
		)
		
	}

}


