		collectionView.addConstraintsProgrammatically
			.pinToSuperSafeArea(inset: UIEdgeInsets(top: 20, left: 16, bottom: 20, right: 16))
		configureCollectionView()
		update([UIImage(named: "mapobject_40_available")!,
